import { cond, sub, divide, multiply, add, block, set, greaterOrEq, proc } from '../base';
var internalTiming = proc(function (clock, time, frameTime, position, finished, toValue, duration, nextProgress, progress, newFrameTime) {
  var state = {
    time: time,
    finished: finished,
    frameTime: frameTime,
    position: position
  };
  var config = {
    duration: duration,
    toValue: toValue
  };
  var distanceLeft = sub(config.toValue, state.position);
  var fullDistance = divide(distanceLeft, sub(1, progress));
  var startPosition = sub(config.toValue, fullDistance);
  var nextPosition = add(startPosition, multiply(fullDistance, nextProgress));
  return block([cond(greaterOrEq(newFrameTime, config.duration), [set(state.position, config.toValue), set(state.finished, 1)], set(state.position, nextPosition)), set(state.frameTime, newFrameTime), set(state.time, clock)]);
});
export default function timing(clock, state, config) {
  if (config.duration === 0) {
    // when duration is zero we end the timing immediately
    return block([set(state.position, config.toValue), set(state.finished, 1)]);
  }

  var lastTime = cond(state.time, state.time, clock);
  var newFrameTime = add(state.frameTime, sub(clock, lastTime));
  var nextProgress = config.easing(divide(newFrameTime, config.duration));
  var progress = config.easing(divide(state.frameTime, config.duration));
  return internalTiming(clock, state.time, state.frameTime, state.position, state.finished, config.toValue, config.duration, nextProgress, progress, newFrameTime);
}
